import React , {useState} from 'react';
import ExpenseItem from './ExpenseItem'
import './Expenses.css';
import ExpensesFilter from './ExpensesFilter';
import Card from '../UI/Card';

const Expenses = (props) =>
{
    const [year, setSelctedYear] = useState('2020');
    const yearSetHandler = (selectedYear) =>
    {
        setSelctedYear(selectedYear);
    }
    const expenses = props.expenses;
    return(
        <Card className="expenses">
            <ExpensesFilter selected={year} onYearSet={yearSetHandler}/>
            {props.items.map(
                expense => <ExpenseItem title={expense.title} amount={expense.amount} date={expense.date}/>)}
        </Card>
    )
}

export default Expenses;
