import React , {useState} from 'react';
import ExpenseItem from './ExpenseItem'
import './Expenses.css';
import ExpensesFilter from './ExpensesFilter';
import Card from '../UI/Card';

const Expenses = (props) =>
{
    const [year, setSelctedYear] = useState('2021');

    const yearSetHandler = (selectedYear) =>
    {
        setSelctedYear(selectedYear);      
    }
    props.items.filter((item) => item.date.getFullYear() == selectedYear);
    return(
        <Card className="expenses">
            <ExpensesFilter selected={year} onYearSet={yearSetHandler}/>
            {filteredItems.map(
                (expense) => (<ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}/>))}
        </Card>
    )
}

export default Expenses;
